import React from "react";
import {TouchableOpacity, FlatList, SafeAreaView, Text, Button, Modal} from "react-native";

const DATA = [
    {
        id: 0,
        name: "Antoine",
        amount: 0.0
    },
    {
        id: 1,
        name: "Clovis",
        amount: 0.0
    },
    {
        id: 2,
        name: "Florent",
        amount: 0.0
    }
];

export default class Members extends React.Component {
    constructor() {
        super();
        this.state = {
            title: "",
            show: false
        }
    }

    render() {
        function onEdit() {
            this.setState({
                title: "Edit"
                , show: true
            })
        }

        function onAdd() {
            this.setState({
                title: "Add"
                , show: true
            })
        }

        const renderItem = ({item}) => {
            return (
                <TouchableOpacity style={styles.title} onPress={onEdit()}>
                    <Text style={styles.item}>{item.name}</Text>
                    <Text style={styles.item}>{item.amount}$</Text>
                </TouchableOpacity>
            );
        };

        return (
            <SafeAreaView style={styles.container}>
                <Button title="Nouveau membre" onPress={onAdd()}/>
                <FlatList
                    data={DATA}
                    renderItem={renderItem}/>
                <Modal transparent={true} visible={this.state.show}>
                    <Text>{this.state.title}</Text>
                </Modal>
            </SafeAreaView>
        );
    }
};

const styles = {
    container: {
        flex: 1
    },
    item: {
        padding: 5,
        marginHorizontal: 7,
        marginVertical:5
    },
    title: {
        fontSize: 32,
        justifyContent: 'space-between',
        flex: 1,
        flexDirection: 'row'
    },
};
