import React from "react";
import {TouchableOpacity, FlatList, SafeAreaView, Text, Button} from "react-native";

const DATA = [
    {
        id: 0,
        name: "Antoine",
        amount: 0.0
    },
    {
        id: 1,
        name: "Clovis",
        amount: 0.0
    },
    {
        id: 2,
        name: "Florent",
        amount: 0.0
    },
];

const App = () => {

    function onEdit() {
        alert('Test')
    }

    function onAdd() {

    }

    const renderItem = ({item}) => {
        return (
            <TouchableOpacity style={styles.title} onPress={onEdit}>
                <Text style={styles.item}>{item.name}</Text>
                <Text style={styles.item}>{item.amount}$</Text>
            </TouchableOpacity>
        );
    };

    return (
        <SafeAreaView style={styles.container}>
            <Button title="Nouveau membre" onPress={onAdd}/>
            <FlatList
                data={DATA}
                renderItem={renderItem}/>
        </SafeAreaView>
    );
};

const styles = {
    container: {
        flex: 1
    },
    item: {
        padding: 5,
        marginHorizontal: 7,
        marginVertical:5
    },
    title: {
        fontSize: 32,
        justifyContent: 'space-between',
        flex: 1,
        flexDirection: 'row'
    },
};

export default App;
