import React from "react";
import {View, TouchableOpacity, FlatList, SafeAreaView, Text, Button, Modal} from "react-native";
import Styles from "../../Styles";

const DATA = [
    {
        id: 0,
        name: "Antoine",
        amount: 0.0
    },
    {
        id: 1,
        name: "Clovis",
        amount: 0.0
    },
    {
        id: 2,
        name: "Florent",
        amount: 0.0
    }
];

let state = {
    title: "Edit",
    show: false
}

export default class Members extends React.Component {
    render() {
        function onEdit() {
            state = {
                title: "Edit"
                , show: true
            }
        }

        function onAdd() {
            state = {
                title: "Add"
                , show: true
            }
        }

        const renderItem = ({item}) => {
            return (
                <TouchableOpacity style={styles.title} onPress={onEdit}>
                    <Text style={styles.item}>{item.name}</Text>
                    <Text style={styles.item}>{item.amount}$</Text>
                </TouchableOpacity>
            );
        };

        return (
            <SafeAreaView style={styles.container}>
                <Button title="Nouveau membre" onPress={onAdd}/>
                <FlatList
                    data={DATA}
                    renderItem={renderItem}/>
                <Modal transparent={true} visible={state.show}>
                    <View style={styles.modal.primary}>
                        <Text>Salut</Text>
                    </View>
                </Modal>
            </SafeAreaView>
        );
    }
};

const styles = {
    container: {
        flex: 1
    },
    item: {
        padding: 5,
        marginHorizontal: 7,
        marginVertical:5
    },
    title: {
        fontSize: 32,
        justifyContent: 'space-between',
        flex: 1,
        flexDirection: 'row'
    },
    modal: {
        primary: {
            backgroundColor: Styles.colors.second,
            margin: 50,
            borderRadius: 10,
            padding: 50,
            flex: 1
        }
    }
};
