import React from "react";
import {View, TouchableOpacity, FlatList, SafeAreaView, Text, Button, Modal, TextInput} from "react-native";

const DATA = [
    {
        id: 0,
        name: "Antoine",
        amount: 0.0
    },
    {
        id: 1,
        name: "Clovis",
        amount: 0.0
    },
    {
        id: 2,
        name: "Florent",
        amount: 0.0
    }
];


class Members extends React.Component {
    constructor() {
        super();
        this.state = {
            title: "NULL",
            show: false
        }
    }

    initModal(title, id) {
        this.setState({
            title: title,
            id: id,
            show: true
        })

        if (DATA.length === id) {
            DATA[DATA.length] = {
                id: id,
                name: "",
                amount: 0.0
            }
        }
    }

    renderItem = ({item}) => {
        return (
            <TouchableOpacity style={styles.title} onPress={() => this.initModal("Edit", item.id)}>
                <Text style={styles.item}>{item.name}</Text>
                <Text style={styles.item}>{item.amount}$</Text>
            </TouchableOpacity>
        );
    };

    render() {
        return (
            <SafeAreaView style={styles.container}>
                <Button title="Nouveau membre" onPress={() => this.initModal("Ajouté un membre", DATA.length)}/>
                <FlatList
                    data={DATA}
                    renderItem={this.renderItem}/>
                <Modal transparent={true} visible={this.state.show}>
                    <View style={styles.modal.primary}>
                        <View style={styles.modal.second}>
                            <Text style={styles.modal.text1}>{this.state.title}</Text>
                            <TextInput
                                onChangeText={(text)=> DATA[this.state.id].name = text}
                                value={() => DATA[this.state.id].name}
                            >Name</TextInput>
                        </View>
                    </View>
                </Modal>
            </SafeAreaView>
        );
    }
};

const styles = {
    container: {
        flex: 1
    },
    item: {
        padding: 5,
        marginHorizontal: 7,
        marginVertical:5,
    },
    title: {
        fontSize: 32,
        justifyContent: 'space-between',
        flex: 1,
        flexDirection: 'row'
    },
    modal: {
        primary: {
            backgroundColor: '#000000aa',
            flex: 1
        },
        second: {
            backgroundColor: 'lightgrey',
            margin: 50,
            borderRadius: 10,
            padding: 40,
            flex: 1
        },
        text1: {
            textAlign: 'center'
        }
    }
};

export default Members
