import React from "react";
import {TouchableOpacity, FlatList, SafeAreaView, Text} from "react-native";

const DATA = [
    {
        name: "First Item",
        amount: 0
    },
    {
        name: "",
        amount: 0
    },
    {
        name: "",
        amount: 0
    },
];

const App = () => {

    function onClick() {
        alert('Test')
    }

    const renderItem = ({member}) => {
        return (
            <TouchableOpacity style={styles.title} onPress={()=>onClick()}>
                <Text style={styles.item}>{member.name}</Text>
                <Text style={styles.item}>{member.amount} + "$"</Text>
            </TouchableOpacity>
        );
    };

    return (
        <SafeAreaView style={styles.container}>
            <FlatList
                data={DATA}
                renderItem={renderItem}/>
        </SafeAreaView>
    );
};

const styles = {
    container: {
        flex: 1
    },
    item: {
        padding: 5,
        marginHorizontal: 7,
        marginVertical:5
    },
    title: {
        fontSize: 32,
        justifyContent: 'space-between',
        flex: 1,
        flexDirection: 'row'
    },
};

export default App;
